import React, { useState } from "react";
import "./Menu.css";

function Menu({ btnState }) {
  return (
    <ul className={`nav-menu ${btnState ? "active" : ""}`}>
      <li className="nav-item">
        <a>Ana Sayfa</a>
      </li>
      <li className="nav-item">
        <a>Hakkımızda</a>
      </li>
      <li className="nav-item">
        <a href="#hakkimizda">Ürünlerimiz</a>
      </li>
      <li className="nav-item">
        <a>İletişim</a>
      </li>
    </ul>
  );
}

export default Menu;
