import * as React from "react";
import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { height } from "@mui/system";
import "./Tabs.css";
import { blue, red } from '@mui/material/colors';

function TabPanel(props) {
  const { children, value, index, ...other } = props;


  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

export default function BasicTabs() {
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{ width: "100%" }} className="tabs-container">
      <Box sx={{}} className="tabs-button-container">
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
          textColor="inherit"
          className="tabs-button-sub-container"
          sx={{
            '& .MuiTabs-indicator': { backgroundColor: "#D2001A",height:"100%",borderRadius:"50px" },
            '& .MuiTab-root': { color: "#000000",zIndex:"1000",opacity:"1",textTransform:"none"},
            '& .Mui-selected': { color: "#FFFFFF"},

          }}
        >
          <Tab label="Cilt Temizliği" {...a11yProps(0)} />
          <Tab label="Yüz Bakımı" {...a11yProps(1)} />
          <Tab label="Vücut Bakımı" {...a11yProps(2)} />
          <Tab label="Kitler" {...a11yProps(3)} />
          <Tab label="Saç Bakımı" {...a11yProps(4)} />

        </Tabs>
      </Box>
      <TabPanel value={value} index={0} sx={{height:"30vh",width:"100%"}}>
      <div className="panel1-container">
        l
        <div className="panel1-left-container"></div>
        <div className="panel1-right-container"></div>
      </div>
      </TabPanel>
      <TabPanel value={value} index={1} >
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2} >
        Item Three
      </TabPanel>
      <TabPanel value={value} index={3} >
        Item four
      </TabPanel>
      <TabPanel value={value} index={4} >
        Item five
      </TabPanel>
    </Box>
  );
}
