import * as React from "react";
import PropTypes from "prop-types";
import Tabs from "@mui/material/Tabs";
import Tab from "@mui/material/Tab";
import Typography from "@mui/material/Typography";
import Box from "@mui/material/Box";
import { height } from "@mui/system";
import "./Tabs.css";

function TabPanel(props) {
  const { children, value, index, ...other } = props;

  const theme = createMuiTheme({
    overrides: {
      MuiTab: {
        root: {
          "&.Mui-selected": {
            background: "red"
          }
        }
      }
    }
  });

  return (
    <div
      role="tabpanel"
      hidden={value !== index}
      id={`simple-tabpanel-${index}`}
      aria-labelledby={`simple-tab-${index}`}
      {...other}
    >
      {value === index && (
        <Box sx={{ p: 3 }}>
          <Typography>{children}</Typography>
        </Box>
      )}
    </div>
  );
}

TabPanel.propTypes = {
  children: PropTypes.node,
  index: PropTypes.number.isRequired,
  value: PropTypes.number.isRequired,
};

function a11yProps(index) {
  return {
    id: `simple-tab-${index}`,
    "aria-controls": `simple-tabpanel-${index}`,
  };
}

export default function BasicTabs() {
  const [value, setValue] = React.useState(0);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Box sx={{ width: "100%" }} className="tabs-container">
      <Box sx={{}} className="tabs-button-container">
        <Tabs
          value={value}
          onChange={handleChange}
          aria-label="basic tabs example"
          textColor="inherit"
          className="tabs-button-sub-container"
          sx={{}}
        >
          <Tab label="Item One" {...a11yProps(0)} />
          <Tab label="Item Two" {...a11yProps(1)} />
          <Tab label="Item Three" {...a11yProps(2)} />
        </Tabs>
      </Box>
      <TabPanel value={value} index={0} className="content-container">
        Item One
      </TabPanel>
      <TabPanel value={value} index={1} className="content-container">
        Item Two
      </TabPanel>
      <TabPanel value={value} index={2} className="content-container">
        Item Three
      </TabPanel>
    </Box>
  );
}
