import React, { useState } from "react";
import "./Menu.css";
import {Link} from 'react-scroll';

function Menu({ btnState }) {
  return (
    <ul className={`nav-menu ${btnState ? "active" : ""}`}>
      <li className="nav-item">
        <a>Ana Sayfa</a>
      </li>
      <li className="nav-item">
        <Link to="hakkimizda" spy={true} smooth={true} offset={-40} duration={500}>Hakkımızda</Link>
      </li>
      <li className="nav-item">
        <a href="#hakkimizda">Ürünlerimiz</a>
      </li>
      <li className="nav-item">
        <a>İletişim</a>
      </li>
    </ul>
  );
}

export default Menu;
